import React, { useState } from "react";
import classes from "./App.module.css"; // Import the CSS module

const SpecialPage = ({ code }) => {
  return (
    <div className={classes.heading}>
      <h2>Welcome to the Special Page!</h2>
      <p>Your unique code is: {code}</p>
      {/* Add your special content here */}
    </div>
  );
};

const HomePage = ({ onCodeSubmit }) => {
  const [code, setCode] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onCodeSubmit(code);
  };

  return (
    <div>
      <h2>Welcome to the Website!</h2>
      <form onSubmit={handleSubmit}>
        <input
          className={classes.input}
          type="text"
          value={code}
          onChange={(e) => setCode(e.target.value)}
          placeholder="Enter your unique code"
        />
        <button className={classes.button} type="submit">
          Submit
        </button>
      </form>
    </div>
  );
};

const App = () => {
  const [isCodeSubmitted, setIsCodeSubmitted] = useState(false);
  const [submittedCode, setSubmittedCode] = useState("");

  const handleCodeSubmit = (code) => {
    setIsCodeSubmitted(true);
    setSubmittedCode(code);
  };

  return (
    <div classname="input">
      {isCodeSubmitted ? (
        <SpecialPage code={submittedCode} />
      ) : (
        <HomePage onCodeSubmit={handleCodeSubmit} />
      )}
    </div>
  );
};

export default App;
